export function buildWarning(message, gravity = 'warning') {
  let alreadyWarned = false;
  return (...args) => {
    if (!alreadyWarned) {
      alreadyWarned = true;
      if (gravity === 'error') {
        console.error(message(...args));
      } else {
        console.warn(message(...args));
      }
    }
  };
}